#include <bits/stdc++.h>using namespace std;using ll = long long ; //------------------------------------------------------// Function: decimalToBinary// Purpose : Converts a decimal number to binary string.// Note    : Handles n = 0 properly now.//------------------------------------------------------string decimalToBinary(int n){    if (n == 0) return "0";  // handle zero case    string s = "";    int i = 0 ;        while(n > 0){        s = to_string(n % 2) + s;   // prepend remainder bit        n /= 2;        i ++;    }        return s;} //------------------------------------------------------// Function: solve()// Purpose : Your main logic — finds the sequence of XORs//           to transform 'a' into 'b'.//------------------------------------------------------void solve(){    ll a , b;    cin >> a >> b;        // If both numbers are already equal → 0 operations    if(b == a){        cout << "0\n\n";        return ;    }        // Convert to binary    string ba = decimalToBinary(a);    string bb = decimalToBinary(b);        // If 'b' has more bits than 'a' → not possible    if(ba.size() < bb.size()){        cout << "-1\n";        return ;    }        // Padding zeros to make both binary strings equal length    string c = "";    for(int i = 0 ; i < (ba.size() - bb.size()) ; i++){        c += "0";    }    bb = c + bb ;        vector<ll> ans;   // to store XOR operation values        //------------------------------------------------------    // Compare each bit from rightmost to leftmost    //------------------------------------------------------    for(int i = bb.size() - 1 ; i >= 0 ; i --){        if(ba[i] == bb[i]){            continue ;  // bit already matches        }        else{            // Calculate XOR mask for this bit position            ll x = 1LL << (bb.size() - i - 1);                        // (Your original condition — kept intact)            if(x > a){                ans.push_back(-1);            }                        // Perform XOR operation            a = a ^ x;                        // Record the XOR value            ans.push_back(x);        }    }        //------------------------------------------------------    // Print results properly    //------------------------------------------------------    cout << ans.size() << "\n";    for(auto v : ans) cout << v << " ";    cout << "\n";         return ;} //------------------------------------------------------// main()//------------------------------------------------------int main() {	int t ;	cin >> t ;		while(t --){	   solve();	}		return 0;}